package Java_Tutorials;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int i=0;
		while(i<100)
		{
			System.out.println(i);
			
			if(i==20)
			{
				System.out.println("statement came till 20");
				break;
			}
			i++;
		
		
		}
		
		
		
		
		
		
	}

}
