package Demo_Java_Basics;

public class Demo_Switch_condition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 2;
		switch (i) {

		case 1:
			System.out.println("This is case 1 program");
			break;
		case 2:
			System.out.println("This is secound program");
			break;
		case 3:
			System.out.println("This is Third program");
			break;
		default:
			System.out.println("This is a default program'");
			break;
		}

	}

}
